mapper.insert:

insert into campaign_image (created_date, last_Modified_Date, image, campaign_id, file_size, image_id, file_path, del_yn
)
values (
    now(), now(), #{image}, #{campaignId}, #{fileSize}, #{imageId}, #{filePath}, 'N'
)

mapper.update:
update files
    set file_name = #{fileName},
        file_path = #{filePath},
        file_size = #{fileSize},
        type = #{type},
        parent_table = #{parentTable}
        parent_no =#{parentNo}
where id= #{id}

mapper.select:
select * 
from files
where parent_table = #{parentTable}
and parent_no = #{parentNo}

mapper.delete:
delete 
from files
where parent_table = #{parentTable}
and parent_no = #{parentNo}
