mapper.insert:



insert into files (
    file_type, target_id, target_type, original_name, saved_name, 
    file_path, file_size, file_extension, created_by, updated_by
)
values (
    #{fileType}, #{targetId}, #{targetType}, #{originalName}, #{savedName}, 
    #{filePath}, #{fileSize}, #{fileExtension}, #{createdBy}, #{updatedBy}
)

mapper.update:
update files
    set file_name = #{fileName},
        file_path = #{filePath},
        file_size = #{fileSize},
        type = #{type},
        parent_table = #{parentTable}
        parent_no =#{parentNo}
where id= #{id}

mapper.select:
select * 
from files
where parent_table = #{parentTable}
and parent_no = #{parentNo}

mapper.delete:
delete 
from files
where parent_table = #{parentTable}
and parent_no = #{parentNo}
